
$(function()
{
	$( ".draggable" ).draggable();

	$(".draggable").mouseup(function()
	{			
//		var color = $(this).css('backgroundColor');
	var index = $(this).index();
		$( ".droppable" ).droppable({drop: function( event, ui )
		{
			var parind= $(this).parent().index();
			$( this ).addClass( "ui-state-highlight" );
			//$(this).css('backgroundColor',color);
			//$(this).hide();
			//$(' .droppable'+parind+' img.droppable').eq(index).show();
	
			$(' .droppable'+parind+' img.droppable').attr('src','img/sofa1/s'+parind+''+(index+1)+'.png');
		}
		});
			$(this).css({'left':'0px', 'top':'0px'})
	});
});
